mapper.select:
SELECT 

FROM sup_channel a

WHERE a.id = #{id}

SELECT
FROM sup_channel a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.channel_name LIKE '%'||#{channelName}||'%' '%'+#{channelName}+'%' concat('%',#{channelName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM sup_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sup_channel SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
