mapper.select:
SELECT 

FROM sup_inventory a

WHERE a.id = #{id}

SELECT
FROM sup_inventory a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.inven_model LIKE '%'||#{invenModel}||'%' '%'+#{invenModel}+'%' concat('%',#{invenModel},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM sup_inventory a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sup_inventory SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
