mapper.select:
SELECT 

FROM sup_sub_order a

WHERE a.id = #{id}

SELECT 

FROM sup_sub_order a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.p_id = #{pId}




		ORDER BY ${page.orderBy}

SELECT 

FROM sup_sub_order a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sup_sub_order SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE p_id = #{pId}
